import { useState, useEffect } from "react";
import Axios from "axios";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
  Table,
  Button,
  DropdownButton,
  Dropdown,
} from "react-bootstrap";

/**
 * Component to display user information
 */
export default function Info() {
  const api = "http://127.0.0.1:3001";

  const [users, setUsers] = useState([]); // State variable to store user data
  const [selectedType, setSelectedType] = useState("all"); // State variable to store the currently selected user type
  const [selectedUsers, setSelectedUsers] = useState([]); // State variable to store the selected users to delete
  
  useEffect(() => {
    Axios.get(`${api}/users`).then((res) => { // Send GET request to /users endpoint to retrieve user data
      setUsers(res.data); // Update state variable with retrieved data
    });
  }, []);

  /**
   * Function to open image URL in a new window
   * @param imageUrl - URL of the image to open
   */
  const openImage = (image) => {
    window.open(image, "_blank"); // Open image URL in a new window when user clicks "View Image" button
  };

  /**
   * Function to handle user type selection from dropdown menu
   * @param type - Selected user type
   */
  const handleTypeSelect = (type) => {
    setSelectedType(type); // Update state variable with the selected user type
  };

  /**
   * Function to handle checkbox selection
   * @param id - ID of the checkbox that was selected
   */
  const handleCheckboxSelect = (id) => {
    const newSelectedUsers = [...selectedUsers];
    if (newSelectedUsers.includes(id)) {
      newSelectedUsers.splice(newSelectedUsers.indexOf(id), 1); // Remove user ID from selectedUsers array if it is already selected
    } else {
      newSelectedUsers.push(id); // Add user ID to selectedUsers array if it is not already selected
    }
    setSelectedUsers(newSelectedUsers); // Update state variable with new selectedUsers array
  };

  /**
   * Function to handle delete button click
   */
  const handleDeleteClick = () => {
    Axios.delete(`${api}/users`, { data: { ids: selectedUsers } }).then((res) => { // Send DELETE request to /users endpoint with selected user IDs in request body
      setUsers(res.data); // Update state variable with updated user data after deletion
      setSelectedUsers([]); // Clear selectedUsers array
    });
  };

  const filteredUsers =
    selectedType === "all"
      ? users
      : users.filter((user) => user.type === selectedType); // Filter user data based on selected user type

  /**
   * Function to delete a user from the table and the database
   * @param id - ID of the user to delete
   */
  const deleteUser = (id) => {
    Axios.delete(`${api}/users/${id}`).then((res) => { // Send DELETE request to /users/:id endpoint to delete user from the database
      setUsers(users.filter((user) => user._id !== id)); // Update state variable with updated user data after deletion
    });
  };

  return (
    <>
      <Container>
        {/* Dropdown menu to filter users by type */}
        <div className="my-3 d-flex justify-content-end">
          <DropdownButton
            id="dropdown-basic-button"
            title={`Filter by Type: ${selectedType}`}
          >
            <Dropdown.Item onClick={() => handleTypeSelect("all")}>
              All
            </Dropdown.Item>
            <Dropdown.Item onClick={() => handleTypeSelect("student")}>
            Student
            </Dropdown.Item>
            <Dropdown.Item onClick={() => handleTypeSelect("graduate")}>
            Graduate
            </Dropdown.Item>
            <Dropdown.Item onClick={() => handleTypeSelect("employee")}>
            Employee
            </Dropdown.Item>
          </DropdownButton>
        </div>

        {/* Table to display user information */}
        <Table striped bordered hover className="my-3">
          <thead>
            <tr>
              <th>Select</th>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Type</th>
              <th>Image</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map(({ _id, name, phone, email, type, image }) => { // Map through filtered user data and rendereach user's information in a table row
              return (
                <tr key={_id}>
                  <td>
                    <input
                      type="checkbox"
                      checked={selectedUsers.includes(_id)}
                      onChange={() => handleCheckboxSelect(_id)}
                    />
                  </td>
                  <td>{name}</td>
                  <td>{email}</td>
                  <td>{phone}</td>
                  <td>{type}</td>
                  <td>
                    {image && ( // If user has an image URL, render "View Image" button
                      <Button
                        variant="link"
                        onClick={(e) => {
                          e.preventDefault(); // prevent the default button behavior
                         openImage(image); // call the openImage function with the imageUrl argument
                        }}
                      >
                        View Image
                      </Button>
                    )}
                  </td>
                  <td>
                    <Button
                      variant="danger"
                      onClick={() => deleteUser(_id)}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>

        {/* Delete button */}
        {selectedUsers.length > 0 && (
          <Button onClick={handleDeleteClick}>Delete Selected Users</Button>
        )}
      </Container>
    </>
  );
}
