import React, { useState } from "react";
import { Container, Form, Button, Table } from "react-bootstrap";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function UserManagementSystem() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [type, setType] = useState("");
  const [category, setCategory] = useState("");
  const [employeeType, setEmployeeType] = useState("");
  const [yearOfGraduate, setYearOfGraduate] = useState(null);
  const [image, setImage] = useState(null);
  const [users, setUsers] = useState([]);

  /*
  * Validate email address
  * @param email - Email address to validate
  * @returns Boolean indicating whether the email address is valid or not
  */
  const validateEmail = (email) => {
    const re = /\S+@\S+\.\S+/;
    return re.test(email);
  }

  /*
  * Handle email input change and validate the email address
  * @param e - Input change event object
  */
  const handleEmailChange = (e) => {
    const email = e.target.value;
    setEmail(email);
    if (validateEmail(email)) {
      setEmailError("");
    } else {
      setEmailError("Please enter a valid email address");
    }
  }

  /*
  * Handle phone input change and format the phone number
  * @param e - Input change event object
  */
  const handlePhoneChange = (e) => {
    const phone = e.target.value;
    const formattedPhone = phone
      .replace(/\D/g, "")
      .replace(/(\d{3})(\d{3})(\d{4})/, "($1) $2-$3");
    setPhone(formattedPhone);
  }

  /*
  * Handle form submission and create a new user
  * @param e - Form submission event object
  */
  const createUser = (e) => {
    e.preventDefault();
    const newUser = {
      id: new Date().getTime(),
      name,
      email,
      phone,
      password,
      type,
      category,
      employeeType,
      yearOfGraduate: yearOfGraduate?.getFullYear(),
      image: image?.name,
    };
    setUsers([...users, newUser]);
    setName("");
    setEmail("");
    setPhone("");
    setPassword("");
    setType("");
    setCategory("");
    setEmployeeType("");
    setYearOfGraduate(null);
    setImage(null);
  }

  /*
  * Handle user deletion
  * @param id - ID of the user to be deleted
  */
  const deleteUser = (id) => {
    const updatedUsers = users.filter((user) => user.id !== id);
    setUsers(updatedUsers);
  }

  return (
    <div className="App">
      <Container>
        <h1>User Management System</h1>
        <Form onSubmit={createUser}>
          <Form.Group controlId="formName">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={handleEmailChange}
              isInvalid={emailError}
            />
            <Form.Control.Feedback type="invalid">
              {emailError}
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group controlId="formPhone">
            <Form.Label>Phone</Form.Label>
            <Form.Control
              type="tel"
              placeholder="Enter phone number"
              value={phone}
              onChange={handlePhoneChange}
            />
          </Form.Group>
          <Form.Group controlId="formPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formType">
            <Form.Label>Type</Form.Label>
            <Form.Control
              as="select"
              value={type}
              onChange={(e) => setType(e.target.value)}
            >
              <option value="">Select type</option>
              <option value="student">Student</option>
              <option value="employee">Employee</option>
              <option value="graduate">Graduate</option>
            </Form.Control>
          </Form.Group>
          {type === "student" && (
            <Form.Group controlId="formCategory">
              <Form.Label>Category</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter category"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              />
            </Form.Group>
          )}
          {type === "employee" && (
            <Form.Group controlId="formEmployeeType">
              <Form.Label>Employee Type</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter employee type"
                value={employeeType}
                onChange={(e) => setEmployeeType(e.target.value)}
              />
            </Form.Group>
          )}
          {type === "graduate" && (
            <Form.Group controlId="formYearOfGraduate">
              <Form.Label>Year of Graduate</Form.Label>
              <DatePicker
                className="form-control"
                selected={yearOfGraduate}
                onChange={(date) => setYearOfGraduate(date)}
                dateFormat="yyyy"
                showYearPicker
              />
            </Form.Group>
          )}
          <Form.Group controlId="formImage">
            <Form.Label>Profile Image</Form.Label>
            <Form.File
              id="custom-file"
              label="Choose file"
              onChange={(e) => setImage(e.target.files[0])}
              custom
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Create User
          </Button>
        </Form>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Type</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={user.id}>
                <td>{index + 1}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.phone}</td>
                <td>{user.type}</td>
                <td>
                  <Button
                    variant="danger"
                    onClick={() => deleteUser(user.id)}
                  >
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>
    </div>
  );
}

export default UserManagementSystem;
