import { useState, useEffect } from "react";
import Axios from "axios";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
  Table,
  Button,
} from "react-bootstrap";

export default function Info() {
  const api = "http://127.0.0.1:3001";

  const [users, setUsers] = useState([]);
  const [userId, setUserId] = useState("");

  useEffect(() => {
    const userId = window.localStorage.getItem("userID");
    setUserId(userId);
    Axios.get(`${api}/users`).then((res) => {
      const user = res.data.find((x) => x._id === userId);
      setUsers(user ? [user] : []);
    });
  }, []);

  const openImage = (e, imageUrl) => {
    e.preventDefault();
    window.open(imageUrl, "_blank");
  };

  return (
    <>
      <header>
        <nav>
          <ul>
            <li>
              <a href="/">Admin</a>
            </li>
            <li>
              <a href="/app">User</a>
            </li>
            <li>
              <a href="/app">User Info</a>
            </li>
          </ul>
        </nav>
      </header>
      <Container>
        <Table striped bordered hover className="my-3">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Type</th>
              <th>Image</th>
            </tr>
          </thead>
          <tbody>
            {users.map(({ _id, name, phone, email, type, imageUrl }) => {
              return (
                <tr key={_id}>
                  <td>{name}</td>
                  <td>{email}</td>
                  <td>{phone}</td>
                  <td>{type}</td>
                  <td>
                    {imageUrl && (
                      <Button
                        variant="link"
                        onClick={(e) => openImage(e, imageUrl)}
                      >
                        View Image
                      </Button>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </Container>
    </>
  );
}
