import React, { useState, useEffect } from "react";
import Axios from "axios";
import { useCookies } from "react-cookie";

import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Form, Button } from "react-bootstrap";
import Info from './Info'

const Auth = () => {
  const [cookies, setCookies] = useCookies(["access_token"]);
  const [registered, setRegistered] = useState(false);
  const [userInfo, setUserInfo] = useState(null);

  const removeCookies = () => {
    setCookies("access_token", "");
    window.localStorage.removeItem("adminID");
    window.location.reload(false);
  };

  const handleRegister = () => {
    setRegistered(true);
  };

  useEffect(() => {
    const getUserInfo = async () => {
      try {
        const response = await Axios.get("http://localhost:3001/user-info", {
          headers: {
            Authorization: `Bearer ${cookies.access_token}`,
          },
        });
        setUserInfo(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    if (cookies.access_token) {
      getUserInfo();
    }
  }, [cookies.access_token]);

  return (
    <>
      <header>
        <nav>
          <ul>
            <li>
              <a href="/">Admin</a>
            </li>
            <li>
              <a href="/app">User</a>
            </li>
            <li>
              {userInfo ? (
                <a href="/info">{userInfo.username}</a>
              ) : (
                <a href="/info">User Info</a>
              )}
            </li>
          </ul>
        </nav>
      </header>
      <div>
        {cookies.access_token ? (
          <Button variant="danger" onClick={removeCookies}>
            Logout
          </Button>
        ) : (
          <>
            {registered ? <Login /> : <Register handleRegister={handleRegister} />}
          </>
        )}
      </div>
    </>
  );
};

const Register = ({ handleRegister }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const onSubmit = async (e) => {
    e.preventDefault();
    await Axios.post("http://localhost:3001/register", { username, password });
    alert("Admin Created");
    handleRegister();
  };

  return (
    <Container>
      <Form className="form" onSubmit={onSubmit}>
        <h2 className="text-white">Register</h2>
        <div className="form-group">
          <label htmlFor="username">Username: </label>
          <Form.Control
            type="text"
            id="username"
            value={username}
            placeholder="username"
            onChange={(e) => setUsername(e.target.value)}
          ></Form.Control>

          <label htmlFor="password">Password: </label>
          <Form.Control
            type="password"
            id="password"
            value={password}
            placeholder="password"
            onChange={(e) => setPassword(e.target.value)}
          ></Form.Control>

          <Button variant="success" type="submit">
            Register
          </Button>
        </div>
      </Form>
    </Container>
  );
};

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showInfo, setShowInfo] = useState(false);

  const [_, setCookies] = useCookies(["access_token"]);

  const onSubmit = async (e) => {
    e.preventDefault();
    const response = await Axios.post("http://localhost:3001/login", {
      username,
      password,
    });
    setCookies("access_token", response.data.token);
    window.localStorage.setItem("userID", response.data.AdminID);
    setShowInfo(true);
  };

  return (
    <Container>
      <Form className="form" onSubmit={onSubmit}>
        <h2 className="text-white">Login</h2>
        <div className="form-group">
          <label htmlFor="username">Username: </label>
          <Form.Control
            type="text"
            id="username"
            value={username}
            placeholder="username"
            onChange={(e) => setUsername(e.target.value)}
          ></Form.Control>

          <label htmlFor="password">Password: </label>
          <Form.Control
            type="password"
            id="password"
            value={password}
            placeholder="password"
            onChange={(e) => setPassword(e.target.value)}
          ></Form.Control>

          <Button variant="success" type="submit">
            Login
          </Button>
        </div>
      </Form>
      {showInfo && <Info />}
    </Container>
  );
};

export default Auth;
