import { useState, useEffect } from "react";
import Axios from "axios";
import "./App.css";

export default function Info() {
  const api = "http://127.0.0.1:3001";

  const [users, setUsers] = useState([]);
  const [selectedType, setSelectedType] = useState("all");

  useEffect(() => {
    Axios.get(`${api}/users`).then((res) => {
      setUsers(res.data);
    });
  }, []);

  const openImage = (e, imageUrl) => {
    e.preventDefault();
    window.open(imageUrl, "_blank");
  };

  const handleTypeSelect = (type) => {
    setSelectedType(type);
  };

  const filteredUsers =
    selectedType === "all"
      ? users
      : users.filter((user) => user.type === selectedType);

  return (
    <div className="info-page">
      <header>
        <nav>
          <ul className="nav-links">
            <li>
              <a href="/">Admin</a>
            </li>
            <li>
              <a href="/app">User</a>
            </li>
            <li>
              <a href="/app">User Info</a>
            </li>
          </ul>
        </nav>
      </header>
      <div className="container">
        <div className="filter-section">
          <select
            className="form-select my-3"
            aria-label="Filter by Type"
            value={selectedType}
            onChange={(e) => handleTypeSelect(e.target.value)}
          >
            <option value="all">All</option>
            <option value="Student">Student</option>
            <option value="Graduate">Graduate</option>
            <option value="Employed">Employed</option>
          </select>
        </div>

        <table className="table table-striped table-bordered my-3">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Type</th>
              <th>Image</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map(({ _id, name, phone, email, type, imageUrl }) => {
              return (
                <tr key={_id}>
                  <td>{name}</td>
                  <td>{email}</td>
                  <td>{phone}</td>
                  <td>{type}</td>
                  <td>
                    {imageUrl && (
                      <button
                        className="btn btn-link"
                        onClick={(e) => openImage(e, imageUrl)}
                      >
                        View Image
                      </button>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}
