import React from 'react'
import { useState } from 'react'
import Axios from 'axios';
import {useCookies} from 'react-cookie'

import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
  Form,
  Button,
} from "react-bootstrap";

const Auth = () => {

  const [cookies, setCookies] = useCookies(access_token)
  const removeCookies = ()=> {
    setCookies("access_token", "")
    window.localStorage.removeItem("adminID")
    window.location.reload(false)
  }

  return (
  <div>
    {
          cookies.access_token
          ?<Button variant="danger" onClick={removeCookies}>Logout</Button>
          : (
            <>
                        <Register />
            <Login />
            </>


          )
    }
  </div>
  )
}

const Register = () =>{
  const [username,setUsername] = useState("")
  const [password,setPassword] = useState("")

  const onSubmit = async (e) => {
    e.preventDefault();
    await Axios.post("http://localhost:3001/register",{username,password})
    alert("Admin Created")

    }

  return (
  <AuthForm label= "Register"
  username = {username}
  setUsername = {setUsername}
  password = {password}
  setPassword = {setPassword}
  onSubmit = {onSubmit}
  />
  )
}

const Login = () =>{
  const [username,setUsername] = useState("")
  const [password,setPassword] = useState("")

  const onSubmit = async (e) => {

    const [_ , setCookies] = useCookies(["access_token"])

    e.preventDefault();
    const response = await Axios.post("http://localhost:3001/login",{username,password})
    setCookies("access_token",response.data.token)
    window.localStorage.setItem("userID",response.data.AdminID)
    window.location.reload(false)
    }

  return (
    <AuthForm label= "Login"
    username = {username}
    setUsername = {setUsername}
    password = {password}
    setPassword = {setPassword}
    onSubmit = {onSubmit}
    />
  )
}

const AuthForm = ({label,username,setUsername,password,setPassword,onSubmit}) => {
  return (
    <Container>
      <Form className = "form" onSubmit={onSubmit}>
        <h2 className = "text-white">{label}</h2>
        <div className="form-group">
          <label htmlFor="username">Username: </label>
          <Form.Control type = "text" id = "username" value = {username} placeholder="username" 
          onChange={e => setUsername(e.target.value)}
          ></Form.Control>

          <label htmlFor="password">Password: </label>
          <Form.Control type = "password" id = "password" value = {password} placeholder="password" 
          onChange={e => setPassword(e.target.value)}
          ></Form.Control>

          <Button variant='success' type ="submit">{label}</Button>
        </div>
      </Form>
    </Container>
  )
}

export default Auth
