import { useState, useEffect } from "react";
import Axios from "axios";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
  Table,
  Button,
  DropdownButton,
  Dropdown,
  Form,
} from "react-bootstrap";

/**
 * Component to display user information
 */
export default function Info() {
  const api = "http://127.0.0.1:3001";

  const [users, setUsers] = useState([]); // State variable to store user data
  const [selectedType, setSelectedType] = useState("all"); // State variable to store the currently selected user type
  const [selectedUsers, setSelectedUsers] = useState([]); // State variable to store the currently selected users to delete
  const [selectedUser, setSelectedUser] = useState(null); // State variable to store the ID of the user being edited
  const [editName, setEditName] = useState("");
  const [editEmail, setEditEmail] = useState("");
  const [editPhone, setEditPhone] = useState("");
  const [editType, setEditType] = useState("");
  const [editCategory, setEditCategory] = useState("");
  const [editEmployeeType, setEditEmployeeType] = useState("");
  const [editYearOfGraduate, setEditYearOfGraduate] = useState("");
  const [editImage, setEditImage] = useState("");

  useEffect(() => {
    Axios.get(`${api}/users`).then((res) => { // Send GET request to /users endpoint to retrieve user data
      setUsers(res.data); // Update state variable with retrieved data
    });
  }, []);

  /**
   * Function to open image URL in a new window
   * @param imageUrl - URL of the image to open
   */
  const openImage = (image) => {
    window.open(image, "_blank"); // Open image URL in a new window when user clicks "View Image" button
  };

  /**
   * Function to handle user type selection from dropdown menu
   * @param type - Selected user type
   */
  const handleTypeSelect = (type) => {
    setSelectedType(type); // Update state variable with the selected user type
  };
  
  /**
   * Function to handle checkbox selection
   * @param id - ID of the user to select or deselect
   */
  const handleCheckboxSelect = (id) => {
    if (selectedUsers.includes(id)) {
      setSelectedUsers(selectedUsers.filter((userId) => userId !== id)); // remove user from the selected users array if it is already selected
    } else {
      setSelectedUsers([...selectedUsers, id]); // add user to the selected users array if it is not already selected
    }
  };

  const filteredUsers =
    selectedType === "all"
      ? users
      : users.filter((user) => user.type === selectedType); // Filter user data based on selected user type

  /**
   * Function to delete selected users from the table and the database
   */
  const deleteSelectedUsers = () => {
    Axios.delete(`${api}/users`, { data: { ids: selectedUsers } }).then((res) => { // Send DELETE request to /users endpoint to delete selected users from the database
      setUsers(users.filter((user) => !selectedUsers.includes(user._id))); // Update state variable with updated user data after deletion
      setSelectedUsers([]);
    });
  };

  /**
   * Function to handle editing user information
   * @param id - ID of the user being edited
   */
  const handleEdit = (id) => {
    setSelectedUser(id); // Set the ID of the user being edited
    const user = users.find((user) => user._id === id); // Find the user data for the selected user
    setEditName(user.name);
    setEditEmail(user.email);
    setEditPhone(user.phone);
    setEditType(user.type);
    setEditCategory(user.category || "");
    setEditEmployeeType(user.employeeType || "");
    setEditYearOfGraduate(user.yearOfGraduate || "");
    setEditImage(user.image);
  };

  /**
   * Function to handle submitting the edit user form
   * @param e - Form submit event
   */
  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent the form from submitting

    Axios.put(`${api}/users/${selectedUser}`, { // Send PUT request to /users/:id endpoint to update user information in the database
      name: editName,
      email: editEmail,
      phone: editPhone,
      type: editType,
      category: editCategory,
      employeeType: editEmployeeType,
      yearOfGraduate: editYearOfGraduate,
      image: editImage,
    }).then((res) => {
      const updatedUser = res.data; // Get the updated user data from the response
      const userIndex = users.findIndex((user) => user._id === selectedUser); // Find the index of the selected user in the users array
      const updatedUsers = [...users]; // Create a copy of the users array
      updatedUsers[userIndex] = updatedUser; // Replace the selected user data with the updated user data
      setUsers(updatedUsers); // Update the state variable with the updated users data
      setSelectedUser(null); // Reset the selected user ID
    });

    // Reset the form fields
    setEditName("");
    setEditEmail("");
    setEditPhone("");
    setEditType("");
    setEditCategory("");
    setEditEmployeeType("");
    setEditYearOfGraduate("");
    setEditImage("");
  };

  return (
    <Container>
      <h1 className="text-center my-5">User Information</h1>
      <div className="d-flex justify-content-between mb-3">
        <DropdownButton
          id="dropdown-basic-button"
          title={`Filter by ${selectedType}`}
        >
          <Dropdown.Item onClick={() => handleTypeSelect("all")}>All</Dropdown.Item>
          <Dropdown.Item onClick={() => handleTypeSelect("student")}>
            Student
          </Dropdown.Item>
          <Dropdown.Item onClick={() => handleTypeSelect("employee")}>
            Employee
          </Dropdown.Item>
        </DropdownButton>
        {selectedUsers.length > 0 && (
          <Button onClick={deleteSelectedUsers} variant="danger">
            Delete Selected
          </Button>
        )}
      </div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th></th>
            <th>Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Type</th>
            <th>Addtional In</th>
            <th>Image</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map((user) => (
            <tr key={user._id}>
              <td>
                <Form.Check
                  type="checkbox"
                  onClick={() => handleCheckboxSelect(user._id)}
                />
              </td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>{user.type}</td>
              <td>{user.category || user.employeeType || user.yearOfGraduate}</td>
              <td>
                <Button onClick={() => openImage(user.image)}>View Image</Button>
              </td>
              <td>
                <Button onClick={() => handleEdit(user._id)}>Edit</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      {selectedUser && (
        <div>
          <h2>Edit User Information</h2>
          <Form onSubmit={handleSubmit}>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={editName}
                onChange={(e) => setEditName(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                value={editEmail}
                onChange={(e) => setEditEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="text"
                value={editPhone}
                onChange={(e) => setEditPhone(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Type</Form.Label>
              <Form.Control
                as="select"
                value={editType}
                onChange={(e) => setEditType(e.target.value)}
              >
                <option value="">Select a type</option>
                <option value="student">Student</option>
                <option value="employee">Employee</option>
              </Form.Control>
            </Form.Group>
            {editType === "student" && (
              <Form.Group>
                <Form.Label>Year of Graduate</Form.Label>
                <Form.Control
                  type="text"
                  value={editYearOfGraduate}
                  onChange={(e) => setEditYearOfGraduate(e.target.value)}
                />
              </Form.Group>
            )}
            {editType === "employee" && (
              <Form.Group>
                <Form.Label>Category</Form.Label>
                <Form.Control
                  type="text"
                  value={editCategory}
                  onChange={(e) => setEditCategory(e.target.value)}
                />
              </Form.Group>
            )}
            <Form.Group>
              <Form.Label>Image URL</Form.Label>
              <Form.Control
                type="text"
                value={editImage}
                onChange={(e) => setEditImage(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Save Changes
            </Button>
            <Button
              variant="secondary"
              className="ml-2"
              onClick={() => setSelectedUser(null)}
            >
              Cancel
            </Button>
          </Form>
        </div>
      )}
    </Container>
  );
}
