import { useState, useEffect } from "react";
import Container from "react-bootstrap/Container";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import axios from "axios";

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [editName, setEditName] = useState("");
  const [editEmail, setEditEmail] = useState("");
  const [editPhone, setEditPhone] = useState("");
  const [editType, setEditType] = useState("");
  const [editCategory, setEditCategory] = useState("");
  const [editImage, setEditImage] = useState(null);
  const [file, setFile] = useState(null);

  useEffect(() => {
    axios.get("/users").then((response) => {
      setUsers(response.data);
    });
  }, []);

  const handleSelect = (event, user) => {
    if (event.target.checked) {
      setSelectedUsers([...selectedUsers, user]);
    } else {
      setSelectedUsers(selectedUsers.filter((u) => u._id !== user._id));
    }
  };

  const deleteSelectedUsers = () => {
    axios.post("/users/delete", { users: selectedUsers }).then(() => {
      setUsers(users.filter((u) => !selectedUsers.includes(u)));
      setSelectedUsers([]);
    });
  };

  const handleEdit = (id) => {
    const user = users.find((u) => u._id === id);
    setSelectedUser(user);
    setEditName(user.name);
    setEditEmail(user.email);
    setEditPhone(user.phone);
    setEditType(user.type);
    setEditCategory(user.category);
    setEditImage(user.image);
  };

  const handleNameChange = (event) => {
    setEditName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEditEmail(event.target.value);
  };

  const handlePhoneChange = (event) => {
    setEditPhone(event.target.value);
  };

  const handleTypeChange = (event) => {
    setEditType(event.target.value);
  };

  const handleCategoryChange = (event) => {
    setEditCategory(event.target.value);
  };

  const handleFileSelect = (event) => {
    setFile(event.target.files[0]);
  };

  const handleFileUpload = () => {
    const formData = new FormData();
    formData.append("file", file);
    axios.post("/upload", formData).then((response) => {
      setEditImage(response.data.url);
      setFile(null);
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const updatedUser = {
      _id: selectedUser._id,
      name: editName,
      email: editEmail,
      phone: editPhone,
      type: editType,
      category: editCategory,
      image: editImage,
    };
    axios.post("/users/update", { user: updatedUser }).then(() => {
      const updatedUsers = users.map((u) =>
        u._id === updatedUser._id ? updatedUser : u
      );
      setUsers(updatedUsers);
      setSelectedUser(null);
    });
  };

  return (
    <Container>
      <h1>User Management System</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>
              <Form.Check
                type="checkbox"
                onChange={(event) =>
                  setSelectedUsers(
                    event.target.checked ? users : []
                  )
                }
                checked={
                  selectedUsers.length === users.length &&
                  users.length > 0
                }
                disabled={users.length === 0}
              />
            </th>
            <th>Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Type</th>
            <th>Category</th>
            <th>Image</th>
            <th>Edit</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>
                <Form.Check
                  type="checkbox"
                  onChange={(event) => handleSelect(event, user)}
                  checked={selectedUsers.includes(user)}
                />
              </td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>{user.type}</td>
              <td>{user.category}</td>
              <td>
                {user.image && (
                  <img src={user.image} alt="User" className="table-image" />
                )}
              </td>
<td>
                <Button variant="warning" onClick={() => handleEdit(user._id)}>Edit</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      {selectedUsers.length > 0 && (
        <div className="delete-container">
          <Button variant="danger" onClick={deleteSelectedUsers}>
            Delete selected users
          </Button>
        </div>
      )}
      {selectedUser && (
        <div className="edit-container">
          <h2>Edit User</h2>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter name"
                value={editName}
                onChange={handleNameChange}
              />
            </Form.Group>

            <Form.Group controlId="formEmail">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter email"
                value={editEmail}
                onChange={handleEmailChange}
              />
            </Form.Group>

            <Form.Group controlId="formPhone">
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter phone number"
                value={editPhone}
                onChange={handlePhoneChange}
              />
            </Form.Group>

            <Form.Group controlId="formType">
              <Form.Label>Type</Form.Label>
              <Form.Control
                as="select"
                value={editType}
                onChange={handleTypeChange}
              >
                <option value="">Select type</option>
                <option value="customer">Customer</option>
                <option value="employee">Employee</option>
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="formCategory">
              <Form.Label>Category</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter category"
                value={editCategory}
                onChange={handleCategoryChange}
              />
            </Form.Group>

            <Form.Group controlId="formImage">
              <Form.Label>Image</Form.Label>
              {editImage ? (
                <div>
                  <img src={editImage} alt="User" className="edit-image" />
                  <Button variant="secondary" onClick={() => setEditImage(null)}>Remove Image</Button>
                </div>
              ) : (
                <div>
                  <Form.File
                    id="formImageFile"
                    label="Select an image file"
                    accept="image/*"
                    onChange={handleFileSelect}
                  />
                  <Button variant="primary" onClick={handleFileUpload}>Upload Image</Button>
                </div>
              )}
            </Form.Group>

            <Button variant="primary" type="submit">
              Save Changes
            </Button>
            <Button variant="secondary" onClick={() => setSelectedUser(null)}>Cancel</Button>
          </Form>
        </div>
      )}
    </Container>
  );
}

export default App;
