import { useState, useEffect } from "react";
import Axios from "axios";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
  Table,
  Button,
  DropdownButton,
  Dropdown,
} from "react-bootstrap";

export default function Info() {
  const api = "http://127.0.0.1:3001";

  const [users, setUsers] = useState([]);
  const [selectedType, setSelectedType] = useState("all");

  useEffect(() => {
    Axios.get(`${api}/users`).then((res) => {
      setUsers(res.data);
    });
  }, []);

  const openImage = (e, imageUrl) => {
    e.preventDefault();
    window.open(imageUrl, "_blank");
  };

  const handleTypeSelect = (type) => {
    setSelectedType(type);
  };

  const filteredUsers =
    selectedType === "all"
      ? users
      : users.filter((user) => user.type === selectedType);

  return (
    <>
      <header>
        <nav>
          <ul>
            <li>
              <a href="/">Admin</a>
            </li>
            <li>
              <a href="/app">User</a>
            </li>
            <li>
              <a href="/app">User Info</a>
            </li>
          </ul>
        </nav>
      </header>
      <Container>
        <div className="my-3 d-flex justify-content-end">
          <DropdownButton
            id="dropdown-basic-button"
            title={`Filter by Type: ${selectedType}`}
          >
            <Dropdown.Item onClick={() => handleTypeSelect("all")}>
              All
            </Dropdown.Item>
            <Dropdown.Item onClick={() => handleTypeSelect("Student")}>
            Student
            </Dropdown.Item>
            <Dropdown.Item onClick={() => handleTypeSelect("Graduate")}>
            Graduate
            </Dropdown.Item>
          </DropdownButton>
        </div>

        <Table striped bordered hover className="my-3">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Type</th>
              <th>Image</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map(({ _id, name, phone, email, type, imageUrl }) => {
              return (
                <tr key={_id}>
                  <td>{name}</td>
                  <td>{email}</td>
                  <td>{phone}</td>
                  <td>{type}</td>
                  <td>
                    {imageUrl && (
                      <Button
                        variant="link"
                        onClick={(e) => openImage(e, imageUrl)}
                      >
                        View Image
                      </Button>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </Container>
    </>
  );
}
