import { useState, useEffect } from "react";
import Axios from "axios";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
  Form,
  Button,
} from "react-bootstrap";

export default function App() {

  // URL of the API that this application will use
  const api = "http://127.0.0.1:3001";

  // Set up state variables to hold user data
  const [users, setUsers] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [type, setType] = useState("");
  const [image, setImage] = useState("");

  // Use the useEffect hook to fetch user data when the component mounts
  useEffect(() => {
    Axios.get(`${api}/users`).then((res) => {
      setUsers(res.data);
    });
  }, []);

  /*
  * Handle form submission and create a new user
  * @param e - Form submit event object
  */
  const createUser = (e) => {
    e.preventDefault();
    if (name && phone && email) {
      // Create a new FormData object to hold the form data
      const formData = new FormData();
      formData.append("name", name);
      formData.append("phone", phone);
      formData.append("email", email);
      formData.append("password", password);
      formData.append("type", type);
      formData.append("image", image);

      // Use Axios to make a POST request to the API with the form data
      Axios.post(`${api}/createUser`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
        .then((res) => {
          // Update the users state variable with the new user data
          setUsers([...users, res.data]);
          // Reset the form inputs
          setName("");
          setEmail("");
          setPhone("");
          setPassword("");
          setType("");
          setImage("");
        })
        .catch((error) => {
          console.log(error);
        });
    }
  };
/*----------------------------------Phone-------------------------------------*/
   /*
  * Format the phone number as (XXX) XXX-XXXX
  * @param phone - the phone number to format
  * @returns a formatted phone number string
  */
   const formatPhoneNumber = (phone) => {
    const cleaned = ('' + phone).replace(/\D/g, '');
    const match = cleaned.match(/^(\d{3})(\d{3})(\d{4})$/);
    return match ? '(' + match[1] + ') ' + match[2] + '-' + match[3] : phone;
  }

  /*
  * Handle phone input change and format the phone number
  * @param e - Input change event object
  */
  const handlePhoneChange = (e) => {
    const input = e.target.value.replace(/\D/g, '');
    if (input.length <= 10) {
      setPhone(formatPhoneNumber(input));
    }
  }
/*---------------------------------------------------------------------------*/

/*----------------------------------Email-------------------------------------*/
  /*
  * Validate email address format
  * @param email - the email address to validate
  * @returns true if the email address is valid, false otherwise
  */
  const validateEmail = (email) => {
    const re = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i;
    return re.test(String(email).toLowerCase());
  }

  /*
  * Handle email input change and validate the email address
  * @param e - Input change event object
  */
  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    if (!validateEmail(e.target.value)) {
      e.target.setCustomValidity('Please enter a valid email address');
    } else {
      e.target.setCustomValidity('');
    }
  }
/*---------------------------------------------------------------------------*/

/*----------------------------------Password-------------------------------------*/
  /*
  * Validate password format
  * @param password - the password to validate
  * @returns true if the password is valid, false otherwise
  */
  const validatePassword = (password) => {
    const re = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$/;
    return re.test(String(password));
  }

  /*
  * Handle password input change and validate the password
  * @param e - Input change event object
  */
  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    if (!validatePassword(e.target.value)) {
      e.target.setCustomValidity('Password must contain at least 8 characters, including uppercase, lowercase, and numbers');
    } else {
      e.target.setCustomValidity('');
    }
  }
/*---------------------------------------------------------------------------*/
  /*
  * Handle type selection and show additional category options
  * @param e - Input change event object
  */
  const handleTypeChange = (e) => {
    setType(e.target.value);
    if (e.target.value === 'Student') {
      setCategory('High School');
    } else if (e.target.value === 'Employee') {
      setCategory('Full Time');
    } else if (e.target.value === 'Graduate') {
      setCategory('');
    }
  }

  /*
  * Handle category selection
  * @param e - Input change event object
  */
  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  }

  /*
  * Handle graduate year input change
  * @param e - Input change event object
  */
  const handleGraduateYearChange = (e) => {
    setGraduateYear(e.target.value);
  }

  return (
    <>
      {/* Header section with navigation links */}
      <header>
        <nav>
          <ul>
            <li>
              <a href="/">Admin</a>
            </li>
            <li>
              <a href="/app">User</a>
            </li>
          </ul>
        </nav>
      </header>
      {/* Main content section with form to create new user */}
      <Container>
        <Form className="form" onSubmit={createUser}>
          <Form.Control
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <Form.Control
            type="text"
            placeholder="Phone"
            value={phone}
            onChange={handlePhoneChange}
          />
          <Form.Control
            type="email"
            placeholder="Email"
            value={email}
            onChange={handleEmailChange}
          />
          <Form.Control
            type="password"
            placeholder="Password"
            value={password}
            onChange={handlePasswordChange}
          />
                    <Form.Group controlId="type">
            <Form.Select value={type} onChange={handleTypeChange}>
              <option value="">Select Type</option>
              <option value="Student">Student</option>
              <option value="Graduate">Graduate</option>
              <option value="Employee">Employee</option>
            </Form.Select>
          </Form.Group>
          {type === 'Student' && (
            <Form.Group controlId="category">
              <Form.Select value={category} onChange={handleCategoryChange}>
                <option value="">Select Category</option>
                <option value="High School">High School</option>
                <option value="University">University</option>
              </Form.Select>
            </Form.Group>
          )}
          {type === 'Employee' && (
            <Form.Group controlId="category">
              <Form.Select value={category} onChange={handleCategoryChange}>
                <option value="">Select Category</option>
                <option value="Full Time">Full Time</option>
                <option value="Part Time">Part Time</option>
                <option value="Freelancer">Freelancer</option>
              </Form.Select>
            </Form.Group>
          )}
          {type === 'Graduate' && (
            <Form.Group controlId="graduateYear">
              <Form.Control
                type="text"
                placeholder="Year of Graduate"
                value={graduateYear}
                onChange={handleGraduateYearChange}
              />
            </Form.Group>
          )}
          <Form.Group controlId="image">
            <Form.Control type="file" onChange={(e) => setImage(e.target.files[0])} />
          </Form.Group>
          <Button variant="success" type="submit">
            Create User
          </Button>
        </Form>
      </Container>
    </>
  );
}
