import { useState, useEffect } from "react";
import Axios from "axios";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
  Form,
  Button,
} from "react-bootstrap";

export default function App() {

  // URL of the API that this application will use
  const api = "http://127.0.0.1:3001";

  // Set up state variables to hold user data
  const [users, setUsers] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [type, setType] = useState("");
  const [category, setCategory] = useState("");
  const [employeeType, setEmployeeType] = useState("");
  const [image, setImage] = useState("");
  const [yearOfGraduate, setYearOfGraduate] = useState("");

  // Use the useEffect hook to fetch user data when the component mounts
  useEffect(() => {
    Axios.get(`${api}/users`).then((res) => {
      setUsers(res.data);
    });
  }, []);

  /*
  * Handle form submission and create a new user
  * @param e - Form submit event object
  */
  const createUser = (e) => {
    e.preventDefault();
    if (name && phone && email) {
      // Create a new FormData object to hold the form data
      const formData = new FormData();
      formData.append("name", name);
      formData.append("phone", phone);
      formData.append("email", email);
      formData.append("password", password);
      formData.append("type", type);
      if (type === "student") {
        formData.append("category", category);
      } else if (type === "employee") {
        formData.append("employeeType", employeeType);
      } else if (type === "graduate") {
        formData.append("yearOfGraduate", yearOfGraduate);
      }
      formData.append("image", image);

      // Use Axios to make a POST request to the API with the form data
      Axios.post(`${api}/createUser`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
        .then((res) => {
          // Update the users state variable with the new user data
          setUsers([...users, res.data]);
          // Reset the form inputs
          setName("");
          setEmail("");
          setPhone("");
          setPassword("");
          setType("");
          setCategory("");
          setEmployeeType("");
          setImage("");
          setYearOfGraduate("");
        })
        .catch((error) => {
          console.log(error);
        });
    }
  };

  /*
  * Handle select type change and reset the category, employee type, and year of graduate inputs
  * @param e - Input change event object
  */
  const handleTypeChange = (e) => {
    setType(e.target.value);
    setCategory("");
    setEmployeeType("");
    setYearOfGraduate("");
  }

  /*
  * Handle select category change
  * @param e - Input change event object
  */
  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  }

  /*
  * Handle select employee type change
  * @param e - Input change event object
  */
  const handleEmployeeTypeChange = (e) => {
    setEmployeeType(e.target.value);
  }

  /*
  * Handle input year of graduate change
  * @param e - Input change event object
  */
  const handleYearOfGraduateChange = (e) => {
    setYearOfGraduate(e.target.value);
  }

  /*
  * Format the phone number as (XXX) XXX-XXXX
  * @param phone - the phone number to format
  * @returns a formatted phone number string
  */
  const formatPhoneNumber = (phone) => {
    const cleaned = ('' + phone).replace(/\D/g, '');
    const match = cleaned.match(/^(\d{3})(\d{3})(\d{4})$/);
    return match ? '(' + match[1] + ') ' + match[2] + '-' + match[3] : phone;
  }

  /*
  * Validate email address format
  * @param email - the email address to validate
  * @returns true if the email address is valid, false otherwise
  */
  const validateEmail = (email) => {
    const re = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i;
    return re.test(String(email).toLowerCase());
  }

  /*
  * Validate password format
  * @param password -the password to validate
  * @returns true if the password meets the requirements, false otherwise
  */
  const validatePassword = (password) => {
    // Password must be at least 8 characters long and contain at least one uppercase letter, one lowercase letter, and one number
    const re = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[a-zA-Z\d]{8,}$/;
    return re.test(password);
  }

  return (
    <Container className="mt-5">
      <h1>Create User</h1>
      <Form onSubmit={createUser}>
        <Form.Group>
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" placeholder="Enter name" value={name} onChange={(e) => setName(e.target.value)} />
        </Form.Group>
        <Form.Group>
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </Form.Group>
        <Form.Group>
          <Form.Label>Phone Number</Form.Label>
          <Form.Control type="tel" placeholder="Enter phone number" value={phone} onChange={(e) => setPhone(formatPhoneNumber(e.target.value))} />
        </Form.Group>
        <Form.Group>
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Enter password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </Form.Group>
        <Form.Group>
          <Form.Label>Type</Form.Label>
          <Form.Control as="select" value={type} onChange={handleTypeChange}>
            <option value="">Select type</option>
            <option value="student">Student</option>
            <option value="employee">Employee</option>
            <option value="graduate">Graduate</option>
          </Form.Control>
        </Form.Group>
        {type === "student" &&
          <Form.Group>
            <Form.Label>Category</Form.Label>
            <Form.Control as="select" value={category} onChange={handleCategoryChange}>
              <option value="">Select category</option>
              <option value="freshman">Freshman</option>
              <option value="sophomore">Sophomore</option>
              <option value="junior">Junior</option>
              <option value="senior">Senior</option>
            </Form.Control>
          </Form.Group>
        }
        {type === "employee" &&
          <Form.Group>
            <Form.Label>Employee Type</Form.Label>
            <Form.Control as="select" value={employeeType} onChange={handleEmployeeTypeChange}>
              <option value="">Select employee type</option>
              <option value="full-time">Full-time</option>
              <option value="part-time">Part-time</option>
            </Form.Control>
          </Form.Group>
        }
        {type === "graduate" &&
          <Form.Group>
            <Form.Label>Year of Graduate</Form.Label>
            <Form.Control type="number" placeholder="Enter year of graduate" value={yearOfGraduate} onChange={handleYearOfGraduateChange} />
          </Form.Group>
        }
        <Form.Group>
          <Form.Label>Profile Image</Form.Label>
          <Form.Control type="file" onChange={(e) => setImage(e.target.files[0])} />
        </Form.Group>
        <Button variant="primary" type="submit" disabled={!name || !validateEmail(email) || !validatePassword(password)}>
          Create User
        </Button>
      </Form>
      <hr />
      <h1>Users</h1>
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            <p>Name: {user.name}</p>
            <p>Email: {user.email}</p>
            <p>Phone: {user.phone}</p>
            <p>Type: {user.type}</p>
            {user.type === "student" &&
              <p>Category: {user.category}</p>
            }
            {user.type === "employee" &&
              <p>Employee Type: {user.employeeType}</p>
            }
            {user.type === "graduate" &&
              <p>Year of Graduate: {user.yearOfGraduate}</p>
            }
            {user.image &&
              <img src={`${api}/uploads/${user.image}`} alt="Profile" />
            }
          </li>
        ))}
      </ul>
    </Container>
  );
}
