import React from 'react'
import { useState, useEffect } from 'react'
import Axios from 'axios';
import {useCookies} from 'react-cookie'

import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
  Form,
  Button,
  Table,
} from "react-bootstrap";

const Auth = () => {

  const [cookies, setCookies] = useCookies(['access_token']) // Fixed: access_token is now a string in an array
  const [registered, setRegistered] = useState(false);

  const removeCookies = ()=> {
    setCookies("access_token", "")
    window.localStorage.removeItem("adminID")
    window.location.reload(false)
  }

  const handleRegister = () => {
    setRegistered(true);
  }

  return (
    <>
      <header>
        <nav>
          <ul>
            <li>
              <a href="/">Admin</a>
            </li>
            <li>
              <a href="/app">User</a>
            </li>
            <li>
              <a href="/info">User Info</a>
            </li>
          </ul>
        </nav>
      </header>
      <div>
        {
          cookies.access_token
          ?<Button variant="danger" onClick={removeCookies}>Logout</Button>
          : (
            <>
            {registered ? <Login /> : <Register handleRegister={handleRegister} />}
            </>
          )
        }
      </div>
    </>
  )
}

const Register = ({handleRegister}) =>{
  const [username,setUsername] = useState("")
  const [password,setPassword] = useState("")

  const onSubmit = async (e) => {
    e.preventDefault();
    await Axios.post("http://localhost:3001/register",{username,password})
    alert("Admin Created")
    handleRegister();
  }

  return (
    <Container>
      <Form className = "form" onSubmit={onSubmit}>
        <h2 className = "text-white">Register</h2>
        <div className="form-group">
          <label htmlFor="username">Username: </label>
          <Form.Control type = "text" id = "username" value = {username} placeholder="username" 
          onChange={e => setUsername(e.target.value)}
          ></Form.Control>

          <label htmlFor="password">Password: </label>
          <Form.Control type = "password" id = "password" value = {password} placeholder="password" 
          onChange={e => setPassword(e.target.value)}
          ></Form.Control>

          <Button variant='success' type ="submit">Register</Button>
        </div>
      </Form>
    </Container>
  )
}

const Login = () =>{
  const [username,setUsername] = useState("")
  const [password,setPassword] = useState("")
  const [users, setUsers] = useState([]);

  useCookies // Fixed: access_token is now a string in an array

  useEffect(() => {
    const adminID = window.localStorage.getItem("adminID");
    if (adminID) {
      Axios.get(`http://localhost:3001/users/${adminID}`, {
        headers: {
          Authorization: `Bearer ${cookies.access_token}`,
        },
      }).then((res) => {
        setUsers(res.data ? [res.data] : []);
      });
    }
  }, [cookies.access_token]);

  const openImage = (e, imageUrl) => {
    e.preventDefault();
    window.open(imageUrl, "_blank");
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const response = await Axios.post("http://localhost:3001/login",{username,password})
    setCookies("access_token",response.data.token)
    window.localStorage.setItem("adminID",response.data.AdminID)
    window.location.reload(false)
  }

  return (
    <Container>
      <Form className = "form" onSubmit={onSubmit}>
        <h2 className = "text-white">Login</h2>
        <div className="form-group">
          <label htmlFor="username">Username: </label>
          <Form.Control type = "text" id = "username" value = {username} placeholder="username" 
          onChange={e => setUsername(e.target.value)}
          ></Form.Control>

          <label htmlFor="password">Password: </label>
          <Form.Control type = "password" id = "password" value = {password} placeholder="password" 
          onChange={e => setPassword(e.target.value)}
          ></Form.Control>

          <Button variant='success' type ="submit">Login</Button>
        </div>
      </Form>

      <Table striped bordered hover className="my-3">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Image</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>
                <Button
                  variant="info"
                  onClick={(e) => openImage(e, user.imageUrl)}
                >
                  View
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  )
}

export default Auth
