import React from 'react'
import { useEffect , useState } from 'react'
import Axios from 'axios';
import {useCookies} from 'react-cookie'

import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
  Form,
  Button,
  Table,
} from "react-bootstrap";



function Auth() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  return (
    <div>
      <header>
        <nav>
          <ul>
            <li>
              <a href="/">Admin</a>
            </li>
            <li>
              <a href="/app">User</a>
            </li>
            <li>
              <a href="/app">User Info</a>
            </li>
          </ul>
        </nav>
      </header>

      {!isLoggedIn && (
        <div>
          <h1>Admin Login</h1>
          <button onClick={handleLogin}>Login</button>
        </div>
      )}

      {isLoggedIn && (
        <div>
          <button onClick={handleLogout}>Logout</button>
          <Info />
        </div>
      )}
    </div>
  );
}

const Register = ({handleRegister}) =>{
  const [username,setUsername] = useState("")
  const [password,setPassword] = useState("")

  const onSubmit = async (e) => {
    e.preventDefault();
    await Axios.post("http://localhost:3001/register",{username,password})
    alert("Admin Created")
    handleRegister();
  }

  return (
    <Container>
      <Form className = "form" onSubmit={onSubmit}>
        <h2 className = "text-white">Register</h2>
        <div className="form-group">
          <label htmlFor="username">Username: </label>
          <Form.Control type = "text" id = "username" value = {username} placeholder="username" 
          onChange={e => setUsername(e.target.value)}
          ></Form.Control>

          <label htmlFor="password">Password: </label>
          <Form.Control type = "password" id = "password" value = {password} placeholder="password" 
          onChange={e => setPassword(e.target.value)}
          ></Form.Control>

          <Button variant='success' type ="submit">Register</Button>
        </div>
      </Form>
    </Container>
  )
}

const Login = () =>{
  const [username,setUsername] = useState("")
  const [password,setPassword] = useState("")

  const [ _ , setCookies] = useCookies(['access_token']) // Fixed: access_token is now a string in an array

  const onSubmit = async (e) => {
    e.preventDefault();
    const response = await Axios.post("http://localhost:3001/login",{username,password})
    setCookies("access_token",response.data.token)
    window.localStorage.setItem("userID",response.data.AdminID)
    window.location.reload(false)
  }

  function Info() {
    const api = "http://127.0.0.1:3001";
  
    const [user, setUser] = useState(null);
  
    useEffect(() => {
      Axios.get(`${api}/users/12345`).then((res) => {
        setUser(res.data);
      });
    }, []);
  
    const openImage = (e, imageUrl) => {
      e.preventDefault();
      window.open(imageUrl, "_blank");
    };
  
    if (!user) {
      return <div>Loading...</div>;
    }
  
    return (
      <>
        <header>
          <nav>
            <ul>
              <li>
                <a href="/">Admin</a>
              </li>
              <li>
                <a href="/app">User</a>
              </li>
              <li>
                <a href="/app">User Info</a>
              </li>
            </ul>
          </nav>
        </header>
        <Container>
          <Table striped bordered hover className="my-3">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Type</th>
                <th>Image</th>
              </tr>
            </thead>
            <tbody>
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.phone}</td>
                <td>{user.type}</td>
                <td>
                  {user.imageUrl && (
                    <Button
                      variant="link"
                      onClick={(e) => openImage(e, user.imageUrl)}
                    >
                      View Image
                    </Button>
                  )}
                </td>
              </tr>
            </tbody>
          </Table>
        </Container>
      </>
    );
  }

  return (
    <Container>
      <Form className = "form" onSubmit={onSubmit}>
        <h2 className = "text-white">Login</h2>
        <div className="form-group">
          <label htmlFor="username">Username: </label>
          <Form.Control type = "text" id = "username" value = {username} placeholder="username" 
          onChange={e => setUsername(e.target.value)}
          ></Form.Control>

          <label htmlFor="password">Password: </label>
          <Form.Control type = "password" id = "password" value = {password} placeholder="password" 
          onChange={e => setPassword(e.target.value)}
          ></Form.Control>

          <Button variant='success' type ="submit">Login</Button>
        </div>
      </Form>
    </Container>
  )
}

export default Auth;
