import React from "react";
import { Link } from "react-router-dom"; // import Link here

import styled from 'styled-components';

const StyledDiv = styled.div`
  ${styles.home}  
`;

function Home() {
    return (
      <div className="home"  style={styles.home}>
        <h1 className="title">Welcome!</h1> 
        <h3 className="subtitle">Tell us WHO are you?</h3>
       
        <div className="buttons">
          <Link to="/Auth">
            <button className="btn btn-admin">Admin</button>
          </Link>  
         
          <Link to="/App">    
            <button  className="btn btn-user"> User</button>
          </Link>            
        </div>      
      </div>  
    );
  }
  
  export default Home;
  
  const styles = {
    ".home": {
      textAlign: "center"  // Center the whole page
    },
    
    ".title": {
      color: "#3f51b5"     // Make the title text purple
    },  
  
    ".subtitle": {
      fontSize: "1.2rem"   // Make the subtitle larger
    },
  
    ".btn": {
      padding: "1rem 2rem",
      margin: "1rem",
      fontSize: "1.2rem",
      cursor: "pointer"   // Common button styles
    },
  
    ".btn-admin": {
      backgroundColor: "#3f51b5",  
      color: "white"     // Admin button is purple and white text    
    },
  
    ".btn-user": {
      backgroundColor: "tomato",   
      color: "white"   // User button is red and white text
    }
  }
