import React, { useState } from "react";
import Axios from "axios";
import { useCookies } from "react-cookie";

import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Form, Button } from "react-bootstrap";

const Auth = () => {
  const [cookies, setCookies] = useCookies(["access_token"]);
  const [registered, setRegistered] = useState(false);
  const [userInfo, setUserInfo] = useState(null);

  const removeCookies = () => {
    setCookies("access_token", "");
    window.localStorage.removeItem("adminID");
    window.location.reload(false);
  };

  const handleRegister = () => {
    setRegistered(true);
  };

  const handleLogin = async (response) => {
    setCookies("access_token", response.data.token);
    window.localStorage.setItem("userID", response.data.AdminID);
    const userInfoResponse = await Axios.get("http://localhost:3001/user-info", {
      headers: {
        Authorization: `Bearer ${response.data.token}`,
      },
    });
    setUserInfo(userInfoResponse.data);
  };

  return (
    <>
      <header>
        <nav>
          <ul>
            <li>
              <a href="/">Admin</a>
            </li>
            <li>
              <a href="/app">User</a>
            </li>
            <li>
              {userInfo ? (
                <a href="/info">{userInfo.username}</a>
              ) : (
                <a href="/info">User Info</a>
              )}
            </li>
          </ul>
        </nav>
      </header>
      <div>
        {cookies.access_token ? (
          <Button variant="danger" onClick={removeCookies}>
            Logout
          </Button>
        ) : (
          <>
            {registered ? (
              <Login handleLogin={handleLogin} />
            ) : (
              <Register handleRegister={handleRegister} />
            )}
          </>
        )}
      </div>
      <Info userInfo={userInfo} />
    </>
  );
};

const Register = ({ handleRegister }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const onSubmit = async (e) => {
    e.preventDefault();
    await Axios.post("http://localhost:3001/register", { username, password });
    alert("Admin Created");
    handleRegister();
  };

  return (
    <Container>
      <Form className="form" onSubmit={onSubmit}>
        <h2 className="text-white">Register</h2>
        <div className="form-group">
          <label htmlFor="username">Username: </label>
          <Form.Control
            type="text"
            id="username"
            value={username}
            placeholder="username"
            onChange={(e) => setUsername(e.target.value)}
          ></Form.Control>

          <label htmlFor="password">Password: </label>
          <Form.Control
            type="password"
            id="password"
            value={password}
            placeholder="password"
            onChange={(e) => setPassword(e.target.value)}
          ></Form.Control>

          <Button variant="success" type="submit">
            Register
          </Button>
        </div>
      </Form>
    </Container>
  );
};

const Login = ({ handleLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await Axios.post("http://localhost:3001/login", {
        username,
        password,
      });
      handleLogin(response);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Container>
      <Form className="form" onSubmit={onSubmit}>
        <h2 className="text-white">Login</h2>
        <div className="form-group">
          <label htmlFor="username">Username: </label>
          <Form.Control
            type="text"
            id="username"
            value={username}
            placeholder="username"
            onChange={(e) => setUsername(e.target.value)}
          ></Form.Control>

          <label htmlFor="password">Password: </label>
          <Form.Control
            type="password"
            id="password"
            value={password}
            placeholder="password"
            onChange={(e) => setPassword(e.target.value)}
          ></Form.Control>

          <Button variant="success" type="submit">
            Login
          </Button>
        </div>
      </Form>
    </Container>
  );
};

const Info = ({ userInfo }) => {
  if (!userInfo) {
    return null;
  }

  return (
    <Container>
      <h2>User Info</h2>
      <p>Username: {userInfo.username}</p>
      <p>Email: {userInfo.email}</p>
      <p>Full Name: {userInfo.fullName}</p>
    </Container>
  );
};

export default Auth;
