import React from "react";
import { Link } from "react-router-dom"; // import Link here

const styles = {
    home: {
      textAlign: "center"
    },
    title: {
      color: "white"
    },
    subtitle: {
      fontSize: "1.2rem"
    },
    btn: {
      padding: "1rem 2rem",
      margin: "1rem",
      fontSize: "1.2rem",
      cursor: "pointer"
    },
    "btn-admin": {
      backgroundColor: "#3f51b5",
      color: "white"
    },
    "btn-user": {
      backgroundColor: "tomato",
      color: "white"
    }
  };

function Home() {
    return (
      <div className="home"  style={{...styles.home,...styles.title}}>
        <h1 className="Title">Welcome!</h1> 
        <h3 className="subtitle">Tell us WHO are you?</h3>
       
        <div className="buttons">
          <Link to="/Auth">
            <button className="btn-admin btn">Admin</button>
          </Link>  
         
          <Link to="/App">    
            <button  className="btn-user btn"> User</button>
          </Link>            
        </div>      
      </div>  
    );
  }
  
  export default Home;
